import { addCart } from "./actions";

export const addCartThhunk = (produto) => (dispatch, getStore) => {
  const lista = JSON.parse(localStorage.getItem("@kenzieShop/CART")) || [];

  if (lista.length > 0) {
    const exist = lista.find((el) => el.id === produto.id);
    const novaLista = lista.map((prod) => {
      return exist
        ? { ...prod, quantity: prod.quantity + 1 }
        : { ...prod, quantity: 1 };
    });
  } else {
    lista.push({ ...produto, quantity: 1 });
  }

  localStorage.setItem("@kenzieShop/CART", JSON.stringify(novaLista));

  console.log(novaLista);
  dispatch(addCart(produto));
};
