import styled from "styled-components";

export const Container = styled.div`
  height: 100px;

  .headerBox {
    padding: 1rem;
    width: 100%;
    height: 100%;
    max-width: 1100px;
    margin: auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  h1 {
    font-size: 1.5rem;
  }

  .menu {
    display: flex;
    flex-direction: column;
    row-gap: 10px;

    button {
      width: 120px;
      background-color: #085e9e;
      color: #f3f3f3;
      height: 20px;
      border-radius: 4px;

      font-size: 1rem;
      &:hover {
        cursor: pointer;
      }
    }
  }

  @media (min-width: 1100px) {
    h1 {
      font-size: 2rem;
    }
    .menu {
      flex-direction: row;
      button {
        font-size: 1.5rem;
      }
    }
  }
`;
