import styled from "styled-components";

export const Container = styled.div`
  height: 100px;

  .headerBox {
    width: 100%;
    max-width: 1100px;
    margin: auto;
    display: flex;
    justify-content: space-between;
  }
  h1 {
    font-size: 2rem;
  }

  .menu {
    font-size: 2rem;
  }
`;
