import { CART_ADD } from "./actionTypes";

const initialState = JSON.parse(localStorage.getItem("@kenzieShop/CART")) || [];

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case CART_ADD:
      const { produto } = action;
      return [...state, { ...produto, qunatity: 1 }];

    default:
      return state;
  }
};

export { cartReducer };
