import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  max-width: 310px;
  height: 300px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 1rem;
  .resumoBox {
    display: flex;
    justify-content: space-between;
  }
  button {
    width: 280px;
    height: 40px;
    background-color: #085e9e;
    border-radius: 16px;
    border: 4px solid #fed75c;
    font-size: 1rem;
    &:hover {
      cursor: pointer;
    }
  }
`;
