import { addCart } from "./actions";

export const addCartThhunk = (produto) => (dispatch) => {
  const lista = JSON.parse(localStorage.getItem("@kenzieShop/CART")) || [];

  const novaLista = [...lista, produto];

  localStorage.setItem("@kenzieShop/CART", JSON.stringify(novaLista));

  dispatch(addCart(produto));
};
