import styled from "styled-components";

export const Container = styled.div`
  padding: 1rem;
  width: 300px;
  min-width: 300px;
  background-color: salmon;
  height: 320px;
  display: flex;
  flex-direction: column;
  text-align: left;
  align-items: stretch;
  justify-content: center;

  img {
    background-color: red;
    width: 100%;
    height: 300px;
  }
`;
