import { Container } from "./styles";

const Header = () => {
  const total = JSON.parse(localStorage.getItem("@kenzieShop/CART")).reduce(
    (acc, { quantity }) => {
      acc += quantity;
      return acc;
    },
    0
  );
  return (
    <Container>
      <div className="headerBox">
        <h1>Kenzie Shop</h1>
        <div className="menu">
          <button>`${total} Cart`</button>
          <button>Login</button>
        </div>
      </div>
    </Container>
  );
};

export { Header };
