import { createContext, useState } from "react";

export const CartContext = createContext([]);

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addToCart = (produto) => {
    const lista = JSON.parse(localStorage.getItem("@kenzieShop/CART")) || [];
    const exist = lista.find((prod) => prod.id === produto.id);
    if (exist) {
      console.log("exist");
      const novaLista = lista.map((prod) => {
        return prod.id === produto.id
          ? { ...prod, quantity: prod.quantity + 1 }
          : prod;
      });
      localStorage.setItem("@kenzieShop/CART", JSON.stringify(novaLista));
      setCart([novaLista]);
    } else {
      console.log("nem");
      const novaLista = [...lista, { ...produto, quantity: 1 }];
      localStorage.setItem("@kenzieShop/CART", JSON.stringify(novaLista));
      setCart([novaLista]);
    }
  };

  const removeFromCart = (produto) => {
    const novaLista = cart.filter((prod) => {
      if (prod.id === produto.id) {
        if (prod.quantity > 1) {
          prod.quantity = prod.quantity - 1;
          return prod;
        }
      } else {
        return prod;
      }
    });
    localStorage.setItem("@kenzieShop/CART", JSON.stringify(novaLista));
    setCart([novaLista]);
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};
