import styled from "styled-components";

export const Container = styled.div`
  padding: 1rem;
  width: 300px;
  min-width: 300px;
  background-color: salmon;
  display: flex;
  flex-direction: column;
  row-gap: 10px;
  text-align: left;
  justify-content: center;

  img {
    background-color: #070707;
    border-radius: 1rem;
    margin: auto;
    width: 225px;
    height: 300px;
  }

  button {
    height: 40px;
    border-radius: 4px;
  }
`;
