import { Container } from "./styles";
import { Produto } from "../Produto";

import { useDispatch } from "react-redux";
import { addCartThhunk } from "../../store/modules/cart/thunks";

const ListaProdutos = ({ dataProdutos }) => {
  const dispatch = useDispatch();
  const handleClick = (produto) => {
    dispatch(addCartThhunk(produto));
  };

  return (
    <Container>
      {dataProdutos.map((produto, i) => {
        return <Produto key={i * 2} produto={produto} />;
      })}
    </Container>
  );
};
export { ListaProdutos };
