import { Container } from "./styles";

const Produto = ({ handleClick, produto, isRemovable = false }) => {
  return (
    <Container>
      <img src={produto.img} alt={produto.name} />
      <h2>{produto.name}</h2>
      <p>{`R$ ${produto.price.toFixed(2)}`}</p>
      <button onClick={() => handleClick(produto)}>
        Adicionar ao Carrinho
      </button>
    </Container>
  );
};

export { Produto };
