import { useEffect } from "react";
import { useState } from "react";
import { useSelector } from "react-redux";
import { Container } from "./styles";

const Header = () => {
  const cart = useSelector(({ cart }) => cart);

  const [totalProdutos, setTotalProdutos] = useState(
    cart.reduce((acc, { quantity }) => {
      acc += quantity;
      return acc;
    }, 0)
  );

  useEffect(() => {
    setTotalProdutos(
      cart.reduce((acc, { quantity }) => {
        acc += quantity;
        return acc;
      }, 0)
    );
  }, [cart]);

  // const totalProdutos = cart.reduce((acc, { quantity }) => {
  //   acc += quantity;
  //   return acc;
  // }, 0);
  // console.log(cart);
  // console.log(totalProdutos);

  return (
    <Container>
      <div className="headerBox">
        <h1>Kenzie Shop</h1>
        <div className="menu">
          <button>`{cart ? totalProdutos : 0} Cart`</button>
          <button>Login</button>
        </div>
      </div>
    </Container>
  );
};

export { Header };
