import styled from "styled-components";

export const Container = styled.div`
  height: 100px;

  .headerBox {
    width: 100%;
    height: 100%;
    max-width: 1100px;
    margin: auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  h1 {
    font-size: 2rem;
  }

  .menu {
    button {
      width: 120px;

      font-size: 1rem;
      &:hover {
        cursor: pointer;
      }
    }

    button + button {
      margin-left: 1rem;
    }
  }

  @media (min-width: 1100px) {
    h1 {
      font-size: 2rem;
    }
    .menu {
      button {
        font-size: 1.5rem;
      }
    }
  }
`;
