import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  max-width: 1100px;
  margin: auto;
  background-color: lightblue;
  display: flex;
  justify-content: space-evenly;
  column-gap:16px
  overflow: scroll;

  @media (min-width: 1100px) {
    flex-wrap: wrap;

  } ;
`;
