import { useDispatch } from "react-redux";
import { addCartThhunk, rmvCartThunk } from "../../store/modules/cart/thunks";
import { Container } from "./styles";

const Produto = ({ produto, isRemovable = false }) => {
  const dispatch = useDispatch();

  return (
    <Container>
      <img src={produto.img} alt={produto.name} />
      <h2>{produto.name}</h2>
      <p>{`R$ ${produto.price.toFixed(2)}`}</p>
      {isRemovable ? (
        <button onClick={() => dispatch(rmvCartThunk)}>
          Remover do Carrinho
        </button>
      ) : (
        <button onClick={() => dispatch(addCartThhunk(produto))}>
          {" "}
          Adicionar ao Carrinho
        </button>
      )}
    </Container>
  );
};

export { Produto };
